    <script>
      function azinuth(){
        debugger;
        var east1 = document.getElementById("east1").value;
        var north1  = document.getElementById("north1").value;
        var east2   = document.getElementById("east2").value;              
        var north2  = document.getElementById("north2").value;
        var converge  = document.getElementById("converge").value;
        var str = "http://localhost/final/Azimuth-Calculator.php?";
        str += "east1=" + east1;
        str += "&north1=" + north1;
        str += "&east2=" + east2;
        str += "&north2=" + north2;
        str += "&converge=" + converge;
        window.location.replace(str);
      }
    </script>

    <?php 
      if (isset($_GET['east1']))
      {
        //echo strval($_GET['longitude']);
        //echo $_GET['Latitude'] + ' ';
        //echo $_GET['Azimuth'] + ' ';
        //echo $_GET['Distance'] + ' ';
        $cmd = 'azinuth.py ' /*+  + ' ' + strval($_GET['Latitude']) + ' ' + strval($_GET['Azimuth']) + ' ' + strval($_GET['Distance'])*/;
        $cmd .= strval($_GET['east1']);
